mapper.delete:

delete from favorites
where favorites_id = #{favoritesId,jdbcType=INTEGER}
and blog_id = #{blogId,jdbcType=INTEGER}

mapper.insert:

insert into favorites (favorites_id, blog_id, blog_title
)
values (#{favoritesId,jdbcType=INTEGER}, #{blogId,jdbcType=INTEGER}, #{blogTitle,jdbcType=VARCHAR}
)

mapper.update:

update favorites
set blog_title = #{blogTitle,jdbcType=VARCHAR}
where favorites_id = #{favoritesId,jdbcType=INTEGER}
and blog_id = #{blogId,jdbcType=INTEGER}

mapper.select:

select favorites_id, blog_id, blog_title
from favorites
where favorites_id = #{favoritesId,jdbcType=INTEGER}
and blog_id = #{blogId,jdbcType=INTEGER}
